class Manga {
  constructor(nom, auteur, type, imageURL, videoURL, description, id) {
      this.nom = nom;
      this.auteur = auteur;
      this.type = type;
      this.imageURL = imageURL;  // Correctement défini
      this.videoURL = videoURL;  // Correctement défini
      this.description = description;
      this.id = id;
  }
}